function Todo(props) {
  const [toggle, setToggle] = useState(true);
  const [text, setText] = useState(props.todo[props.key]);

  function toggleInput() {
    setToggle(!toggle);
  }

  function handleChange(event) {
    setText(event.target.value);
  }
  function handleUpdate(){
    const newTodo = {...props.todo}
    newTodo[props.key] = text
    const newList = props.todoList.map((todo)=>{
        if(todo.title === newTodo.title)return newTodo;
        return todo;
    })

    props.updateList(newList)
    setToggle();
  }

  return (
    <div className="App">
      {toggle ? (
        <p onDoubleClick={toggleInput}>{text}</p>
      ) : (
        <div>
        <input type="text" value={text} onChange={handleChange} />
        <button onClick={handleUpdate}>Update</button>
        </div>
      )}
    </div>
  );
}

export default Todo
